draw((-2,-5)--(0,-5),red); label("$y=f(x)$",(0,-5),E);
draw((-2,-6)--(0,-6),cyan); label("$y=g(x)$",(0,-6),E);
draw((-2,-7)--(0,-7),blue); label("$y=h(x)$",(0,-7),E);
