import math;

// Draw triangles
pair A = (0, 1);
pair B = (-cos(1.3962), 0);
pair C = (cos(1.3962), 0);
pair D = (2, 1);
pair E = (2-cos(1.3089), 0);
pair F = (2+cos(1.3089), 0);
draw(A--B--C--cycle);
draw(D--E--F--cycle);
draw(A--D);
draw(C--E);
