[asy]
unitsize(0.8 cm);

pair A, B, C, D, E, F, P;

A = (1,4);
B = (0,0);
C = (6,0);
E = interp(A,C,2/3);
F = interp(A,B,1/5);
P = extension(B,E,C,F);

draw(A--B--C--cycle);
draw(B--E);
draw(C--F);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$E$", E, NE);
label("$F$", F, W);
