path foo = moo(0);
real t;

for (t = 0; t <= 2*pi + 0.01; t = t + 0.01) {
  foo = foo--moo(t);
}

draw(foo,red);

draw((-1,0)--(1,0));
draw((0,-1)--(0,1));
draw((-1,4*sqrt(3)/9)--(1,4*sqrt(3)/9),blue);

label("$r = \sin 2 \theta$", (1.2,0.6), red);
label("$y = \frac{4 \sqrt{3}}{9}$", (-1, 4*sqrt(3)/9), W, blue);
[/asy]

