fill(Arc(O,1/3,0,90)--Xprime--Yprime--cycle,0.7*white);

draw(A--B--C--cycle);
draw(Circle(O, 1/3));
draw((0,1/3)--(2/3,1/3));
draw((1/3,0)--(1/3,2/3));

draw((2/3, 0)--(2/3, 1/3));
draw((1/16,0)--(1/16,1/16)--(0,1/16));

label("$A$",A, SW);
label("$B$",B, down);
label("$C$",C, left);
label("$X$",(1/3,0), down);
label("$Y$",(0,1/3), left);
label("$X'$", (1/3, 2/3), NE);
label("$Y'$", (2/3, 1/3), NE);
label("$X''$", (2/3, 0), down);
label("$O$", O, NE);

[/asy]

