axes(-2,8,-7,3);
xh(3/2,sqrt(7)/2,3,-2,-5.8,1.8);
dot((1,-2)^^(5,-2));
real f(real x) { return -2 + sqrt(7)/3*(x-3); }
draw(graph(f, -1.4, 7.4),dotted);
real g(real x) { return -2 - sqrt(7)/3*(x-3); }
draw(graph(g, -1.4, 7.4),dotted);
[/asy]

