dot((1.5,i+0.5));
}
for (int i=0; i<3; ++i) {
dot((2.5,i+0.5));
}
for (int i=0; i<2; ++i) {
dot((3.5, i+0.5));
}
dot((4.5,0.5));
[/asy]

