draw(graph(g,-pi + 0.01,pi - 0.01),red);
draw(graph(g,pi + 0.01,3*pi - 0.01),red);
limits((-3*pi,-5),(3*pi,5),Crop);
draw((-pi,-5)--(-pi,5),dashed);
draw((pi,-5)--(pi,5),dashed);
trig_axes(-3*pi,3*pi,-5,5,pi/2,1);
layer();
rm_trig_labels(-5, 5, 2);
[/asy]

