draw((0,0,0)--(1,0,0),dashed);
draw((0,0,0)--(0,1,0),dashed);
draw((0,0,0)--(0,0,1),dashed);
draw((0,0,0)--(1/2,1/2,1/2),dashed);
draw((1,0,0)--(1.2,0,0),Arrow3(6));
draw((0,1,0)--(0,1.2,0),Arrow3(6));
draw((0,0,1)--(0,0,1.2),Arrow3(6));

label("$x$", (1.3,0,0));
label("$y$", (0,1.3,0));
