draw((0,-2.5)--(0,2.5));
draw((0,0)--((-2)*dir(67.5)));
draw((0,0)--(2*dir(67.5)),dashed);

dot((-2)*dir(67.5));
dot(2*dir(67.6));

label("$\frac{3 \pi}{8}$", (0.5,0.3));
[/asy]

