pair A = (0,0);
pair B = (2,0);
pair C = (1,sqrt(3));
pair O = (1,sqrt(3)/3);

draw(O--B);
draw(O--C);
draw(O--(B+C)/2);
draw(O--(A+B)/2);

draw(A--B--C--cycle);
draw(circle(O,sqrt(3)/3));

label("$A$",A,SW);
label("$B$",B,SE);
label("$C$",C,N);
label("$D$",(A+B)/2,S);
label("$E$",(B+C)/2,NE);
label("$O$",O,W);
label("$12\sqrt{3}$",(1,-0.2),S);

[/asy]

